package basics;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int i = 1;//initialization
        while(i<7)//condition 
        	{
        	System.out.println("hi! this is jaganath");
        	i++;//increment operator
        }
        int j=5;
        while(j>1)
        {
        	System.out.println("how are you?");
        	j--;
        }
	}

}
